const events = items[0].json;
if (!events || !events.length) {
  return [{json: {
    status: 'failure',
    message: 'No matching appointments found.',
    data: {
      query: $json.query,
      context: $json.context || null
    }
  }}];
}
if (events.length > 1) {
  return [{json: {
    status: 'failure',
    message: 'Multiple matching appointments found. Clarification required.',
    data: {
      matches: events.map(e => ({id: e.id, summary: e.summary, start: e.start, end: e.end}))
    }
  }}];
}
return [{json: {
  status: 'success',
  data: {
    appointment_id: events[0].id,
    summary: events[0].summary,
    start: events[0].start,
    end: events[0].end
  }
}}];

return [{json: {
  status: 'error',
  message: $json.message || 'Unknown error occurred while identifying appointment.',
  data: $json
}}];
