// Extract and validate appointment data from webhook body with enhanced service properties
const body = $input.first().json.body;

// Validate required base fields
const required = ['name', 'date', 'time', 'phone', 'preferred_contact_method', 'service', 'timezone', 'email'];
const missing = required.filter(f => !body[f]);

if (missing.length > 0) {
  return [{
    json: {
      status: 'failure',
      message: `Missing required fields: ${missing.join(', ')}`,
      data: {
        missing_fields: missing,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate email format
if (!body.email || body.email.trim() === '') {
  return [{
    json: {
      status: 'failure',
      message: 'Email is required',
      data: {
        timestamp: new Date().toISOString()
      }
    }
  }];
}

const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(body.email)) {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid email format',
      data: {
        provided_email: body.email,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate preferred_contact_method enum
const validContactMethods = ['phone', 'email', 'text'];
if (!validContactMethods.includes(body.preferred_contact_method.toLowerCase())) {
  return [{
    json: {
      status: 'failure',
      message: `Invalid preferred_contact_method. Must be one of: ${validContactMethods.join(', ')}`,
      data: {
        provided_method: body.preferred_contact_method,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate date format (YYYY-MM-DD)
if (!/^\d{4}-\d{2}-\d{2}$/.test(body.date)) {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid date format. Expected YYYY-MM-DD',
      data: {
        provided_date: body.date,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate time format (HH:MM)
if (!/^\d{2}:\d{2}$/.test(body.time)) {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid time format. Expected HH:MM',
      data: {
        provided_time: body.time,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate timezone format (basic check for IANA timezone)
if (!body.timezone || body.timezone.length < 3) {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid or missing timezone',
      data: {
        provided_timezone: body.timezone,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Pass all data for service property parsing in next node
return [{ json: body }];

// Dynamic service property parsing for enhanced booking schema
const body = $input.first().json;

// Extract selected service from service object
const serviceObj = body.service;
let selectedService = Object.keys(serviceObj).find(key => serviceObj[key] === true);

if (!selectedService) {
  return [{
    json: {
      status: 'failure',
      message: 'No service selected',
      data: {
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Service configuration mapping (slug -> display name)
const serviceMapping = {{SERVICE_MAPPING}};

// Get service display name with proper capitalization
const serviceDisplayName = serviceMapping[selectedService] || selectedService
  .replaceAll('-', ' ')
  .split(' ')
  .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
  .join(' ');

// Extract service properties for the selected service
const serviceProperties = body.service_properties && body.service_properties[selectedService] ? body.service_properties[selectedService] : {};

// Define required properties per service (from config)
const requiredProperties = {{REQUIRED_PROPERTIES}};

// Validate required service properties
const requiredForService = requiredProperties[selectedService] || [];
const missingServiceProps = requiredForService.filter(prop => !serviceProperties[prop]);

if (missingServiceProps.length > 0) {
  return [{
    json: {
      status: 'failure',
      message: `Missing required service properties for ${serviceDisplayName}: ${missingServiceProps.join(', ')}`,
      data: {
        service: selectedService,
        missing_service_properties: missingServiceProps,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Build final appointment data with parsed service info
const appointmentData = {
  // Base appointment fields
  name: body.name,
  date: body.date,
  time: body.time,
  phone: body.phone,
  email: body.email,
  preferred_contact_method: body.preferred_contact_method,
  timezone: body.timezone,
  notes: body.notes || '',

  // Service information
  service: serviceDisplayName,
  service_slug: selectedService,
  service_properties: serviceProperties,

  // Flatten service properties for easy access
  ...serviceProperties
};

return [{ json: appointmentData }];

// Handle system errors from Google Calendar or Sheets APIs
const inputItem = $input.first();
const inputData = inputItem.json || {};
const errorInfo = inputData.error || null;

// Check if this is actually an error
if (errorInfo !== null) {
  // Check if this is success data that shouldn't be here
  // If we got here, it's unclear what happened - create generic error
  return [{
    json: {
      status: 'error',
      message: 'An unexpected error occurred during booking.',
      data: {
        error_type: 'UNKNOWN_ERROR',
        error_name: 'UnexpectedError',
        timestamp: new Date().toISOString(),
        retry_possible: true,
        technical_details: {
          note: 'Error handler triggered without error details',
          input_data: inputData
        }
      }
    }
  }];
} else 
{
   return [];
}

// Format proper error response with actual error information
return [{
  json: {
    status: 'error',
    message: errorInfo.message || 'A system error occurred while booking the appointment.',
    data: {
      error_type: errorInfo.code || 'BOOKING_ERROR',
      error_name: errorInfo.name || 'SystemError',
      timestamp: new Date().toISOString(),
      retry_possible: true,
      technical_details: {
        error_message: errorInfo.message,
        error_stack: errorInfo.stack,
        node_name: errorInfo.node?.name,
        error_code: errorInfo.code,
        http_code: errorInfo.httpCode
      }
    }
  }
}];

const data = $input.first().json;
if (data.status === 'failure') return [{ json: data }];
return [];

return {
  status: 'failure',
  message: 'Appointment time already taken.',
  data: $input.first().json
};

// Format final data for Google Sheets with calendar event ID
const bookingData = $input.first().json;
const calendarEvent = $input.last().json;

// Update CSV data with Google Calendar Event ID and Modified timestamp
const updatedCsvData = {
  ...bookingData.csv_data,
  'Google Calendar Event ID': calendarEvent.id || '',
  'Modified': new Date().toISOString() // Update modification timestamp
};

// Return merged data ready for Google Sheets
return [{
  json: {
    ...bookingData,
    ...calendarEvent,
    csv_data: updatedCsvData
  }
}];

// Calculate booking duration and create timezone-aware datetime strings for Google Calendar API
const booking = $input.first().json;

// Pass through failure status
if (booking.status === 'failure') {
  return [{ json: booking }];
}

const durationMinutes = booking.duration;
const timezone = booking.timezone; // IANA timezone (e.g., "America/Chicago")

// Parse start time components from the booking time string (HH:MM format)
const [startHours, startMinutes] = booking.time.split(':').map(Number);

// Calculate end time using pure arithmetic (avoids JavaScript Date timezone conversion)
// This keeps calculations in the LOCAL timezone specified by the caller
let totalMinutes = startHours * 60 + startMinutes + durationMinutes;
const daysToAdd = Math.floor(totalMinutes / (24 * 60)); // Handle day overflow
const endHours = Math.floor(totalMinutes / 60) % 24;
const endMinutes = totalMinutes % 60;

// Format end time
const endTime = `${String(endHours).padStart(2, '0')}:${String(endMinutes).padStart(2, '0')}`;

// Calculate end date (handle appointments that cross midnight)
let endDate = booking.date;
if (daysToAdd > 0) {
  const startDateObj = new Date(booking.date + 'T00:00:00');
  startDateObj.setDate(startDateObj.getDate() + daysToAdd);
  endDate = startDateObj.toISOString().split('T')[0];
}

// Helper function to convert local datetime + timezone â†’ RFC3339 format
function toRFC3339(dateTimeString, timeZone) {
  // Create a date object from the local datetime string
  const localDateTime = new Date(dateTimeString);
  if (isNaN(localDateTime.getTime())) {
    throw new Error(`Invalid datetime string: ${dateTimeString}`);
  }

  // Use Intl.DateTimeFormat to get timezone offset for the specific date
  const formatter = new Intl.DateTimeFormat('en-US', {
    timeZone,
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false
  });

  const parts = formatter.formatToParts(localDateTime);
  const getValue = (type) => parseInt(parts.find(p => p.type === type).value, 10);

  // Create a date in the target timezone
  const targetDate = new Date(Date.UTC(
    getValue('year'),
    getValue('month') - 1,
    getValue('day'),
    getValue('hour'),
    getValue('minute'),
    getValue('second')
  ));

  // Calculate the offset in minutes
  const offsetMinutes = (targetDate.getTime() - localDateTime.getTime()) / 60000;
  const sign = offsetMinutes <= 0 ? '-' : '+';
  const absOffset = Math.abs(offsetMinutes);
  const offsetHours = String(Math.floor(absOffset / 60)).padStart(2, '0');
  const offsetMins = String(absOffset % 60).padStart(2, '0');
  const offset = `${sign}${offsetHours}:${offsetMins}`;

  // Format the final RFC3339 string
  const [date, time] = dateTimeString.split('T');
  const timePart = time && time.split(':').length === 2 ? `${time}:00` : time;
  return `${date}T${timePart}${offset}`;
}

// Create timezone-aware datetime strings in RFC3339 format for Google Calendar API
const startDatetimeString = `${booking.date}T${booking.time}:00`;
const endDatetimeString = `${endDate}T${endTime}:00`;

// Convert to RFC3339 format with timezone offset
const startRFC3339 = toRFC3339(startDatetimeString, timezone);
const endRFC3339 = toRFC3339(endDatetimeString, timezone);

return [{
  json: {
    ...booking,
    start_datetime: startRFC3339,
    end_datetime: endRFC3339,
    end_date: endDate,
    end_time: endTime
  }
}];

const data = $input.first().json;

// Pass through failure status
if (data.status === 'failure') return [{ json: data }];

return [{
  json: {
    status: 'success',
    message: "Booked appointment.",
    data: data
  }
}];

// Handle system errors from Google Calendar or Sheets APIs
const inputItem = $input.first();
const inputData = inputItem.json || {};
const errorInfo = inputData.error || null;

// Check if this is actually an error
if (errorInfo !== null) {
  // Check if this is success data that shouldn't be here
  // If we got here, it's unclear what happened - create generic error
  return [{
    json: {
      status: 'error',
