   return [];
}

// Format proper error response with actual error information
return [{
  json: {
    status: 'error',
    message: errorInfo.message || 'A system error occurred while booking the appointment.',
    data: {
      error_type: errorInfo.code || 'BOOKING_ERROR',
      error_name: errorInfo.name || 'SystemError',
      timestamp: new Date().toISOString(),
      retry_possible: true,
      technical_details: {
        error_message: errorInfo.message,
        error_stack: errorInfo.stack,
        node_name: errorInfo.node?.name,
        error_code: errorInfo.code,
        http_code: errorInfo.httpCode
      }
    }
  }
}];

let out = [];

for (const item of $input.all()) {
  out.push({
    service: item.json["Service Type"],
    duration: item.json["Duration (minutes)"]
  });
}

return out;

// Map service properties to CSV format with proper column naming
const data = $input.first().json;

// Get current timestamp for audit fields
const currentTimestamp = new Date().toISOString();

// Base CSV columns matching generated schema
const csvData = {
  'Name': data.name,
  'Phone': data.phone,
  'Email': data.email,
  'Preferred Contact Method': data.preferred_contact_method,
  'Date': data.date,
  'Time': data.time,
  'Service': data.service,
  'Duration': data.duration || '',
  'Status': 'Confirmed',
  'Notes': data.notes || '',
  'Google Calendar Event ID': '', // Will be updated after calendar creation
  'Created': currentTimestamp,
  'Modified': currentTimestamp
};

// Service property column mapping (slug -> proper column names) - matches config.json services
const columnMapping = {{COLUMN_MAPPING}};

// Add service-specific properties to CSV data
if (data.service_slug && data.service_properties && columnMapping[data.service_slug]) {
  const serviceColumns = columnMapping[data.service_slug];

  Object.keys(serviceColumns).forEach(propKey => {
    const columnName = serviceColumns[propKey];
    const value = data.service_properties[propKey];
    csvData[columnName] = value !== undefined ? value : '';
  });
}

// Create Google Calendar event description with service properties
let description = `Service: ${data.service}\n`;
if (data.service_properties && Object.keys(data.service_properties).length > 0) {
  description += '\nService Details:\n';
  Object.keys(data.service_properties).forEach(key => {
    const value = data.service_properties[key];
    if (value !== undefined && value !== '') {
      const displayKey = key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
      description += `${displayKey}: ${value}\n`;
    }
  });
}
if (data.notes) {
  description += `\nNotes: ${data.notes}`;
}

// Add customer information to description
description += `\n--\n\nCustomer: ${data.name}\nPreferred Contact: ${data.preferred_contact_method}\nPhone: ${data.phone}\nEmail: ${data.email}`;

// Return enhanced appointment data with CSV mapping and calendar description
return [{
  json: {
    ...data,
    csv_data: csvData,
    calendar_description: description
  }
}];
