// Extract and validate modification data from webhook body
const body = $input.first().json.body;

// Validate required appointment_id
if (!body.appointment_id) {
  return [{
    json: {
      status: 'failure',
      message: 'Missing required field: appointment_id',
      data: {
        missing_fields: ['appointment_id'],
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate updates object exists
if (!body.updates || typeof body.updates !== 'object') {
  return [{
    json: {
      status: 'failure',
      message: 'Missing or invalid updates object',
      data: {
        timestamp: new Date().toISOString()
      }
    }
  }];
}

const updates = body.updates;

// Validate required service field in updates (needed for duration lookup)
if (!updates.service) {
  return [{
    json: {
      status: 'failure',
      message: 'Missing required field in updates: service (needed for duration lookup)',
      data: {
        missing_fields: ['updates.service'],
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Validate date format if provided (YYYY-MM-DD)
if (updates.date && !/^\d{4}-\d{2}-\d{2}$/.test(updates.date)) {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid date format. Expected YYYY-MM-DD',
      data: {
        provided_date: updates.date,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Handle ASAP/urgent time requests and validate time format
if (updates.time) {
  // Check for ASAP-type requests (these should be resolved by conversation flow, but handle gracefully)
  const asapKeywords = ['asap', 'as soon as possible', 'urgent', 'earliest', 'soonest', 'next available'];
  const timeStr = String(updates.time).toLowerCase();

  if (asapKeywords.some(keyword => timeStr.includes(keyword))) {
    return [{
      json: {
        status: 'failure',
        message: 'ASAP time request needs specific time resolution. Please provide exact time in HH:MM format.',
        data: {
          provided_time: updates.time,
          suggestion: 'Use conversation flow to resolve ASAP requests to specific times',
          timestamp: new Date().toISOString()
        }
      }
    }];
  }

  // Validate standard time format (HH:MM)
  if (!/^\d{2}:\d{2}$/.test(updates.time)) {
    return [{
      json: {
        status: 'failure',
        message: 'Invalid time format. Expected HH:MM',
        data: {
          provided_time: updates.time,
          timestamp: new Date().toISOString()
        }
      }
    }];
  }
}

// Validate timezone format if provided
if (updates.timezone && updates.timezone.length < 3) {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid timezone format',
      data: {
        provided_timezone: updates.timezone,
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Extract and validate service from updates object (required field)
let selectedService = updates.service;

// Handle service object with boolean flags or direct string
if (typeof selectedService === 'object') {
  selectedService = Object.keys(selectedService).find(key => selectedService[key] === true);
  if (!selectedService) {
    return [{
      json: {
        status: 'failure',
        message: 'No service selected in service object',
        data: {
          timestamp: new Date().toISOString()
        }
      }
    }];
  }
} else if (typeof selectedService !== 'string') {
  return [{
    json: {
      status: 'failure',
      message: 'Invalid service format. Expected object with boolean flags or string.',
      data: {
        timestamp: new Date().toISOString()
      }
    }
  }];
}

// Normalize service slug (convert spaces to hyphens for consistency)
const serviceSlug = selectedService.toLowerCase().replaceAll(' ', '-');

// Clean service name for display (convert hyphens to spaces and capitalize first letter of each word)
const serviceDisplayName = selectedService
  .replaceAll('-', ' ')
  .split(' ')
  .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
  .join(' ');

// Create clean updates object with only provided fields
const cleanUpdates = {};

// Only include fields that were actually provided in the request
if (updates.name) cleanUpdates.name = updates.name;
if (updates.date) cleanUpdates.date = updates.date;
if (updates.time) cleanUpdates.time = updates.time;
if (updates.phone) cleanUpdates.phone = updates.phone;
if (serviceDisplayName) cleanUpdates.service = serviceDisplayName;
if (updates.timezone) cleanUpdates.timezone = updates.timezone;
if (updates.notes !== undefined) cleanUpdates.notes = updates.notes;

// Add flag to indicate if time was provided for downstream processing
const timeProvided = !!updates.time;

// Success: Return validated data with service for duration lookup
return [{
  json: {
    appointment_id: body.appointment_id,
    service: serviceDisplayName,       // For display and CSV
    service_slug: serviceSlug,          // For duration lookup (matches Service Types sheet)
    updates: cleanUpdates,
    time_provided: timeProvided
  }
}];

// Dynamic service property parsing for enhanced modification schema
const input = $input.first().json;

// Extract updates object and service information
const appointmentId = input.appointment_id;
const serviceSlug = input.service_slug;  // Always present (required field)
const serviceDisplayName = input.service; // Always present
const updates = input.updates || {};
const timeProvided = input.time_provided;

// Service configuration mapping (slug -> display name)
const serviceMapping = {{SERVICE_MAPPING}};

// Extract service properties if provided
const servicePropsInput = input.service_properties || {};
const serviceProperties = servicePropsInput[serviceSlug] || {};

// Define required properties per service (from config)
const requiredProperties = {{REQUIRED_PROPERTIES}};

// Validate required service properties only if service properties were provided
if (Object.keys(serviceProperties).length > 0) {
  const requiredForService = requiredProperties[serviceSlug] || [];
  const missingServiceProps = requiredForService.filter(prop => !serviceProperties[prop]);

  if (missingServiceProps.length > 0) {
    return [{
      json: {
        status: 'failure',
        message: `Missing required service properties for ${serviceDisplayName}: ${missingServiceProps.join(', ')}`,
        data: {
          service: serviceSlug,
          missing_service_properties: missingServiceProps,
          timestamp: new Date().toISOString()
        }
      }
    }];
  }
}

// Build enhanced modification data
const modificationData = {
  appointment_id: appointmentId,
  service: serviceDisplayName,        // For display and CSV
  service_slug: serviceSlug,          // For duration lookup
  service_properties: serviceProperties,
  updates: updates,
  time_provided: timeProvided,
  // Flatten service properties for easy access
  ...serviceProperties
};

return [{ json: modificationData }];

// Map service properties to CSV column format for appointment modifications
const input = $input.first().json;

// Pass through failure status
if (input.status === 'failure') {
  return [{ json: input }];
}

// CSV column mapping for service properties
const csvServiceColumns = {};

// Map service properties to CSV columns (service is always present)
if (input.service_slug && input.service_properties) {
  const serviceSlug = input.service_slug;
  const serviceDisplayName = input.service;
  const serviceProperties = input.service_properties;

  // Service property to CSV column mapping
  const propertyColumnMappings = {{COLUMN_MAPPING}};

  const mappings = propertyColumnMappings[serviceSlug] || {};

  // Map each service property to its CSV column
  for (const [propertyKey, propertyValue] of Object.entries(serviceProperties)) {
    const csvColumnName = mappings[propertyKey];
    if (csvColumnName && propertyValue) {
      csvServiceColumns[csvColumnName] = propertyValue;
    }
  }
}

// Add CSV columns and service data to output
const output = {
  ...input,
  csv_service_columns: csvServiceColumns
};

return [{ json: output }];

let out = [];

for (const item of $input.all()) {
  out.push({
    service: item.json["Service Type"],
    duration: item.json["Duration (minutes)"]
  });
}

return out;

// Calculate booking duration and create timezone-aware datetime strings for Google Calendar API
// NOW runs AFTER fetching appointment data from sheet, so we have complete information
const inputData = $input.first().json;
const booking = inputData.updates || inputData;
const timeProvided = inputData.time_provided;

// Pass through failure status
if (inputData.status === 'failure') {
  return [{ json: inputData }];
}

// Check if date is being modified in the updates
const dateProvided = booking.hasOwnProperty('date') && booking.date;

// Only check availability if time OR date is being modified
if (!timeProvided && !dateProvided) {
  return [{
    json: {
      ...inputData,
      skip_availability_check: true
    }
  }];
}

// Get time - from updates OR existing appointment data (sheet)
const rawTime = (typeof booking.time === 'string' ? booking.time.trim() : null) || inputData.Time || '';
const hasValidTime = /^(?:[01]?\d|2[0-3]):[0-5]\d$/.test(rawTime);

// Get date - from updates OR existing appointment data (sheet)
const dateCandidate = booking.date || inputData.Date || '';
const hasValidDate = /^\d{4}-\d{2}-\d{2}$/.test(String(dateCandidate));

// If we don't have valid time AND date, we can't check availability
if (!hasValidTime || !hasValidDate) {
  return [{
    json: {
      ...inputData,
      skip_availability_check: true
    }
  }];
}

// Get duration from service data (already merged)
const durationMinutes = Number(inputData.duration) || 60;
const timezone = booking.timezone || inputData.Timezone || 'America/Chicago';

// Parse start time
const [startHours, startMinutes] = rawTime.split(':').map((v) => Number(v));

// Calculate end time
let totalMinutes = startHours * 60 + startMinutes + durationMinutes;
const daysToAdd = Math.floor(totalMinutes / (24 * 60));
const endHours = Math.floor(totalMinutes / 60) % 24;
const endMinutes = totalMinutes % 60;
const endTime = `${String(endHours).padStart(2, '0')}:${String(endMinutes).padStart(2, '0')}`;

// Calculate end date
let endDate = dateCandidate;
if (daysToAdd > 0) {
  const startDateObj = new Date(`${dateCandidate}T00:00:00`);
  startDateObj.setDate(startDateObj.getDate() + daysToAdd);
  endDate = startDateObj.toISOString().split('T')[0];
}

// Helper function for RFC3339 format
function toRFC3339(dateTimeString, timeZone) {
  const localDateTime = new Date(dateTimeString);
  if (isNaN(localDateTime.getTime())) {
    throw new Error(`Invalid datetime string: ${dateTimeString}`);
  }

  const formatter = new Intl.DateTimeFormat('en-US', {
    timeZone,
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false
  });

  const parts = formatter.formatToParts(localDateTime);
  const getValue = (type) => parseInt(parts.find(p => p.type === type).value, 10);

  const targetDate = new Date(Date.UTC(
    getValue('year'),
    getValue('month') - 1,
    getValue('day'),
    getValue('hour'),
    getValue('minute'),
    getValue('second')
  ));

  const offsetMinutes = (targetDate.getTime() - localDateTime.getTime()) / 60000;
  const sign = offsetMinutes <= 0 ? '-' : '+';
  const absOffset = Math.abs(offsetMinutes);
  const offsetHours = String(Math.floor(absOffset / 60)).padStart(2, '0');
  const offsetMins = String(absOffset % 60).padStart(2, '0');
  const offset = `${sign}${offsetHours}:${offsetMins}`;

  const [date, time] = dateTimeString.split('T');
  const timePart = time && time.split(':').length === 2 ? `${time}:00` : time;
  return `${date}T${timePart}${offset}`;
}

// Create RFC3339 datetime strings
const startDatetimeString = `${dateCandidate}T${rawTime}:00`;
const endDatetimeString = `${endDate}T${endTime}:00`;

const startRFC3339 = toRFC3339(startDatetimeString, timezone);
const endRFC3339 = toRFC3339(endDatetimeString, timezone);

return [{
  json: {
    ...inputData,
    start_datetime: startRFC3339,
    end_datetime: endRFC3339,
    end_date: endDate,
    end_time: endTime
  }
}];

return {
  status: 'failure',
  message: 'Appointment time already taken.',
  data: $input.first().json
};

// Enhanced format for calendar event with complete appointment data from sheet
let updatedKeys = Object.keys($input.first().json.updates).map((key)=> key.charAt(0).toUpperCase() + key.slice(1));
let input = $input.first().json;
let updates = input.updates;
let out = input;

delete out.updates;

// Apply updates to existing appointment data
updatedKeys.forEach((updateKey) => {
  if (out[updateKey] !== null && out[updateKey] !== undefined)
  {
    if(updateKey == "Notes")
    {
      // Append new notes to existing notes
      const existingNotes = out["Notes"] || '';
      const newNotes = updates["notes"] || '';
      out[updateKey] = existingNotes ? (existingNotes + '\n' + newNotes) : newNotes;
    }
    else
    {
      out[updateKey] = updates[updateKey.charAt(0).toLowerCase() + updateKey.slice(1)];
    }
  }
  else {
    // Add new field if it doesn't exist
    out[updateKey] = updates[updateKey.charAt(0).toLowerCase() + updateKey.slice(1)];
  }
});

// Helper function to generate calendar description in the exact same format as bookAppointment
function generateComprehensiveCalendarDescription(data) {
  // Create Google Calendar event description with service properties
  let description = `Service: ${data.Service || data.service || 'Service'}\n`;

  // Add service-specific details by looking for service property columns
  const serviceDetails = [];
  Object.keys(data).forEach(key => {
    if (key.includes(' - ') && data[key] && data[key] !== '') {
      // Extract the property name from column format (e.g., "Consultation: Project Type" -> "Project Type")
      const propertyName = key.split(' - ')[1];
      if (propertyName) {
        serviceDetails.push({ key: propertyName, value: data[key] });
      }
    }
  });

  if (serviceDetails.length > 0) {
    description += '\nService Details:\n';
    serviceDetails.forEach(detail => {
      description += `${detail.key}: ${detail.value}\n`;
    });
  }

  // Add notes
  const notes = data.Notes || data.notes || '';
  if (notes) {
    description += `\nNotes: ${notes}`;
  }

  // Add customer information to description (exact same format as bookAppointment)
  description += `\n--\n\nCustomer: ${data.Name || data.name || 'N/A'}`;
  description += `\nPreferred Contact: ${data['Preferred Contact Method'] || data.preferred_contact_method || 'N/A'}`;
  description += `\nPhone: ${data.Phone || data.phone || 'N/A'}`;
  description += `\nEmail: ${data.Email || data.email || 'N/A'}`;

  return description;
}

// Generate comprehensive calendar description using complete appointment data from sheet
out.calendar_description = generateComprehensiveCalendarDescription(out);

// If start_datetime and end_datetime don't exist (Calculate Booking Duration node didn't run),
// calculate them from existing appointment data using the same logic
if (!out.start_datetime || !out.end_datetime) {
  let appointmentDate = out.Date || out.date;
  let appointmentTime = out.Time || out.time;
  const timezone = out.Timezone || out.timezone || 'America/Chicago'; // Default timezone
  const durationMinutes = out.Duration || out.duration || 60; // Default 60 minutes if not provided

  if (appointmentDate && appointmentTime) {
    // Handle different time formats from sheets
    // Check if appointmentTime is already in ISO format (contains 'T' and timezone)
    if (typeof appointmentTime === 'string' && appointmentTime.includes('T')) {
      // Extract just the time portion from ISO format (e.g., "2025-10-18T11:00:00-05:00" -> "11:00")
      const isoDate = new Date(appointmentTime);
      if (!isNaN(isoDate.getTime())) {
        // Convert to HH:MM format in the appointment's timezone
        const timeFormatter = new Intl.DateTimeFormat('en-US', {
          timeZone: timezone,
          hour: '2-digit',
          minute: '2-digit',
          hour12: false
        });
        appointmentTime = timeFormatter.format(isoDate);
      } else {
        // Fallback: try to extract time from string pattern
        const timeMatch = appointmentTime.match(/T(\d{2}:\d{2})/);
        appointmentTime = timeMatch ? timeMatch[1] : appointmentTime;
      }
    }

    // Ensure we have HH:MM format before proceeding
    if (!/^\d{1,2}:\d{2}$/.test(appointmentTime)) {
      console.log(`Invalid time format: ${appointmentTime}, skipping datetime calculation`);
    } else {
      // Parse start time components from the appointment time string (HH:MM format)
      const [startHours, startMinutes] = appointmentTime.split(':').map(Number);

      // Calculate end time using pure arithmetic
      let totalMinutes = startHours * 60 + startMinutes + durationMinutes;
      const daysToAdd = Math.floor(totalMinutes / (24 * 60)); // Handle day overflow
      const endHours = Math.floor(totalMinutes / 60) % 24;
      const endMin = totalMinutes % 60;

      // Format end time
      const endTime = `${String(endHours).padStart(2, '0')}:${String(endMin).padStart(2, '0')}`;

      // Calculate end date (handle appointments that cross midnight)
      let endDate = appointmentDate;
      if (daysToAdd > 0) {
        const startDateObj = new Date(appointmentDate + 'T00:00:00');
        startDateObj.setDate(startDateObj.getDate() + daysToAdd);
        endDate = startDateObj.toISOString().split('T')[0];
      }

      // Helper function to convert local datetime + timezone â†’ RFC3339 format
      function toRFC3339(dateTimeString, timeZone) {
        // Create a date object from the local datetime string
        const localDateTime = new Date(dateTimeString);
        if (isNaN(localDateTime.getTime())) {
          throw new Error(`Invalid datetime string: ${dateTimeString}`);
        }

        // Use Intl.DateTimeFormat to get timezone offset for the specific date
        const formatter = new Intl.DateTimeFormat('en-US', {
          timeZone,
          year: 'numeric',
          month: '2-digit',
          day: '2-digit',
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
          hour12: false
        });

        const parts = formatter.formatToParts(localDateTime);
        const getValue = (type) => parseInt(parts.find(p => p.type === type).value, 10);

        // Create a date in the target timezone
        const targetDate = new Date(Date.UTC(
          getValue('year'),
          getValue('month') - 1,
          getValue('day'),
          getValue('hour'),
          getValue('minute'),
          getValue('second')
        ));

        // Calculate the offset in minutes
        const offsetMinutes = (targetDate.getTime() - localDateTime.getTime()) / 60000;
        const sign = offsetMinutes <= 0 ? '-' : '+';
        const absOffset = Math.abs(offsetMinutes);
        const offsetHours = String(Math.floor(absOffset / 60)).padStart(2, '0');
        const offsetMins = String(absOffset % 60).padStart(2, '0');
        const offset = `${sign}${offsetHours}:${offsetMins}`;

        // Format the final RFC3339 string
        const [date, time] = dateTimeString.split('T');
        const timePart = time && time.split(':').length === 2 ? `${time}:00` : time;
        return `${date}T${timePart}${offset}`;
      }

      // Create timezone-aware datetime strings in RFC3339 format for Google Calendar API
      const startDatetimeString = `${appointmentDate}T${appointmentTime}:00`;
      const endDatetimeString = `${endDate}T${endTime}:00`;

      // Convert to RFC3339 format with timezone offset
      out.start_datetime = toRFC3339(startDatetimeString, timezone);
      out.end_datetime = toRFC3339(endDatetimeString, timezone);
      out.end_date = endDate;
      out.end_time = endTime;
    }
  }
}

// Convert field names to lowercase with underscores for consistent processing
let final = {};

for (let key in out) {
  final[(key.replaceAll(" ", "_")).toLowerCase()] = out[key];
}

return final;

const data = $input.first().json;

// Pass through failure status
if (data.status === 'failure') return [{ json: data }];

return [{
  json: {
    status: 'success',
    message: "Booked appointment.",
    data: data
  }
}];

// Format complete appointment data for Google Sheets update
// This receives merged data from calendar event creation and complete appointment data
const input = $input.first().json;

// Pass through failure status
if (input.status === 'failure') {
  return [{ json: input }];
}

// Extract calendar event ID (preserve existing ID since this is a modification)
const calendarEventId = input.id || input.event_id || input.google_calendar_event_id || input['google_calendar_event_id'] || '';
const appointmentData = input;

// Create comprehensive CSV data object using ALL available data (existing + updates)
const csvData = {
  'Name': appointmentData.name || appointmentData.Name || '',
  'Phone': appointmentData.phone || appointmentData.Phone || '',
  'Email': appointmentData.email || appointmentData.Email || '',
  'Preferred Contact Method': appointmentData.preferred_contact_method || appointmentData['Preferred Contact Method'] || '',
  'Date': appointmentData.date || appointmentData.Date || '',
  'Time': appointmentData.time || appointmentData.Time || '',
  'Service': appointmentData.service || appointmentData.Service || '',
  'Duration': appointmentData.duration || appointmentData.Duration || '',
  'Status': appointmentData.status || appointmentData.Status || 'Confirmed',
  'Notes': appointmentData.notes || appointmentData.Notes || '',
  'Google Calendar Event ID': calendarEventId,
  'Created': appointmentData.created || appointmentData.Created || '',
  'Modified': new Date().toISOString(),
  'row_number': appointmentData.row_number || appointmentData.row_number || '',
};

// Preserve ALL existing service-specific columns from the original appointment
// Look for any columns that contain ' - ' (service-specific format)
Object.keys(appointmentData).forEach(key => {
  if (key.includes(' - ') && appointmentData[key] !== undefined && appointmentData[key] !== null) {
    csvData[key] = appointmentData[key];
  }
});

// Add/update service-specific properties from CSV service columns if new service was provided
if (appointmentData.csv_service_columns) {
  // Use pre-mapped CSV service columns from Map to CSV Format node
  Object.assign(csvData, appointmentData.csv_service_columns);
} else if (appointmentData.service_slug && appointmentData.service_properties) {
  // Fallback: map service properties manually
  const columnMapping = {{COLUMN_MAPPING}};

  const serviceColumns = columnMapping[appointmentData.service_slug];
  if (serviceColumns) {
    Object.keys(serviceColumns).forEach(propKey => {
      const columnName = serviceColumns[propKey];
      const value = appointmentData.service_properties[propKey];
      if (value !== undefined && value !== null) {
        csvData[columnName] = value;
      }
    });
  }
}

// Return complete CSV data ready for Google Sheets update
return [{ json: csvData }];

// Handle system errors from Google Calendar or Sheets APIs
const inputItem = $input.first();
const inputData = inputItem.json || {};
const errorInfo = inputData.error || null;

// Check if this is actually an error
if (errorInfo !== null) {
  // Check if this is success data that shouldn't be here
  // If we got here, it's unclear what happened - create generic error
  return [{
    json: {
      status: 'error',
      message: 'An unexpected error occurred during booking.',
      data: {
        error_type: 'UNKNOWN_ERROR',
        error_name: 'UnexpectedError',
        timestamp: new Date().toISOString(),
        retry_possible: true,
        technical_details: {
          note: 'Error handler triggered without error details',
          input_data: inputData
        }
      }
    }
  }];
} else 
{
   return [];
}

// Format proper error response with actual error information
return [{
  json: {
    status: 'error',
    message: errorInfo.message || 'A system error occurred while booking the appointment.',
    data: {
      error_type: errorInfo.code || 'BOOKING_ERROR',
      error_name: errorInfo.name || 'SystemError',
      timestamp: new Date().toISOString(),
      retry_possible: true,
      technical_details: {
        error_message: errorInfo.message,
        error_stack: errorInfo.stack,
        node_name: errorInfo.node?.name,
        error_code: errorInfo.code,
        http_code: errorInfo.httpCode
      }
    }
  }
}];

